#include "Version.h"
#include "WebKitVersion.h"	// generated by scripts/generate-webkitversion.pl

#include <String.h>


BString webKitVersion()
{
    return BString() << WEBKIT_MAJOR_VERSION << "." << WEBKIT_MINOR_VERSION;
}

int webKitMajorVersion()
{
    return WEBKIT_MAJOR_VERSION;
}

int webKitMinorVersion()
{
    return WEBKIT_MINOR_VERSION;
}
